sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var ga = new GlideAjax('AddressUtils');

	ga.addParam('sysparm_name', 'getAddressInfoByCEP');

	ga.addParam('sysparm_cep', newValue);

	ga.getXML(function(response){

		var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));

		if (!answer.erro) {

			g_form.setValue('address', answer.logradouro);

			g_form.setValue('city', answer.localidade);

			g_form.setValue('neighborhood', answer.bairro);

			g_form.setValue('state', answer.uf);

		}

	});

}
