sys_script_include.script:
var TimeClockTimesheetUtils = Class.create();
TimeClockTimesheetUtils.prototype = {
    initialize: function() {

        this.TASK_TABLE_NAME = 'x_649728_timeclock_task';

        this.TIME_WORKED_TABLE_NAME = 'x_649728_timeclock_time_worked';
    },

    hasTimeWorked: function(timesheetID) {

		var timeWorked = new GlideRecord(this.TIME_WORKED_TABLE_NAME);

		timeWorked.addQuery('timesheet', timesheetID);

		timeWorked.query();

		return timeWorked.hasNext();

    },

    updateTotalCost: function (timesheet) {

		var totalCost = 0;

		var timeWorked = new GlideRecord(this.TIME_WORKED_TABLE_NAME);

		timeWorked.addQuery('timesheet', timesheet.getUniqueValue());

		timeWorked.query();

		while (timeWorked.next()) 

			totalCost += parseFloat(timeWorked.cost);

		timesheet.setValue('total_cost', totalCost);

		timesheet.update();

	},

    type: 'TimeClockTimesheetUtils'
};
