sys_script_include.script:
var TimeClockProjectUtils = Class.create();
TimeClockProjectUtils.prototype = {
    initialize: function() {

        this.TASK_TABLE_NAME = 'x_649728_timeclock_task';

        this.TIME_WORKED_TABLE_NAME = 'x_649728_timeclock_time_worked';
    },

    hasTimeWorked: function(projectID) {

        var taskUtils = new TimeClockTaskUtils();

        var task = new GlideRecord(this.TASK_TABLE_NAME);

        task.addQuery('project', projectID);

        task.query();

        while (task.next()) {

            if (taskUtils.hasTimeWorked(task.getUniqueValue()))

                return true;

        }

        return false;

    },

    updateTotalCost: function(project) {

        var totalCost = 0;

        var task = new GlideRecord(this.TASK_TABLE_NAME);

        task.addQuery('project', project.getUniqueValue());

        task.query();

        while (task.next()) {

			var taskTotalCost = 0;

            var timeWorked = new GlideRecord(this.TIME_WORKED_TABLE_NAME);

            timeWorked.addQuery('task', task.getUniqueValue());

            timeWorked.query();

            while (timeWorked.next())

				taskTotalCost += parseFloat(timeWorked.cost);

            task.setValue('total_cost', taskTotalCost);

            task.update();

			totalCost += taskTotalCost;

        }

		project.setValue('total_cost', totalCost);

		project.update();

    },

    type: 'TimeClockProjectUtils'
};
