sys_script_include.script:
var TimeClockTaskUtils = Class.create();
TimeClockTaskUtils.prototype = {
    initialize: function() {
		this.TIME_WORKED_TABLE_NAME = 'x_649728_timeclock_time_worked';
    },

	hasTimeWorked: function (taskID) {

		var timeWorked = new GlideRecord(this.TIME_WORKED_TABLE_NAME);

		timeWorked.addQuery('task', taskID);

		timeWorked.query();

		return timeWorked.hasNext();

	},

	updateTotalCost: function (task) {

		var totalCost = 0;

		var timeWorked = new GlideRecord(this.TIME_WORKED_TABLE_NAME);

		timeWorked.addQuery('task', task.getUniqueValue());

		timeWorked.query();

		while (timeWorked.next()) 

			totalCost += parseFloat(timeWorked.cost);

		task.setValue('total_cost', totalCost);

		task.update();

	},

    type: 'TimeClockTaskUtils'
};
