sys_script_include.script:
var TimeClockTimeWorkedUtils = Class.create();
TimeClockTimeWorkedUtils.prototype = {
	initialize: function() {
	},

	refQualAssignedTo: function (timeWorked) {

		if (!timeWorked.timesheet.nil()) 

			return 'sys_id=' + timeWorked.timesheet.assigned_to.toString();

		else if (!timeWorked.task.nil()) {

			var refQual = [];

			if (!timeWorked.task.assigned_to.nil())

				refQual.push(timeWorked.task.assigned_to.toString());

			if (!timeWorked.task.additional_assignee_list.nil())

				refQual = refQual.concat(timeWorked.task.additional_assignee_list.toString().split(','));

			return 'sys_idIN' + refQual.toString();

		} else

			return 'roles=d5fd5b5b1b652110ae7a76e7cc4bcb1a';


	},

	refQualTimesheet: function (timeWorked) {

		if (!timeWorked.assigned_to.nil()) 

			return 'assigned_to=' + timeWorked.assigned_to.toString();

		else if (!timeWorked.task.nil()) {

			var refQual = [];

			if (!timeWorked.task.assigned_to.nil())

				refQual.push(timeWorked.task.assigned_to.toString());

			if (!timeWorked.task.additional_assignee_list.nil())

				refQual = refQual.concat(timeWorked.task.additional_assignee_list.toString().split(','));

			return 'assigned_to.sys_idIN' + refQual.toString();

		}

	},

	refQualTask: function (timeWorked) {

		if (!timeWorked.assigned_to.nil())

			return 'stateIN2,3^assigned_to=' + timeWorked.assigned_to.toString();

		if (!timeWorked.timesheet.assigned_to.nil())

			return 'stateIN2,3^assigned_to=' + timeWorked.timesheet.assigned_to.toString();

		return 'stateIN2,3';

	},

	type: 'TimeClockTimeWorkedUtils'
};
